function jsonToUrlEncoded(json)
{
    return Object.keys(json).map(function(k) {
        return k + '=' + encodeURIComponent(json[k]);
    }).join('&');
}

function resolveTemplate(template, response)
{
    var responseJson = JSON.parse(response);
    var html = responseJson.layout.replace('{content}', template);
    for (var k in responseJson.labels) {
        html = html.replace(k, responseJson.labels[k]);
    }
    return html;
}

