 * status = 1 (widget is not ready (only css or js loaded))
 * status = 2 (widget ready (css and js loaded))
 * @param widget
 * @param step
 */
function incStatus(widget, step)
{
    widget.status = widget.status || 0;
    widget.status += (step || 1);

    if (widget.status == 2) {
        var readyEvent = createNewEvent('ready');
        widget.dispatchEvent(readyEvent);
    }
}

