    var closeButtons = container.getElementsByClassName('of-popup-tos-close');

    for (var i = 0; i < closeButtons.length; i++) {
        closeButtons[i].onclick = function() {
            container.style.display = 'none';
        };
    }

    var tabs = container.getElementsByClassName('of-tab-item');
    var contents = container.getElementsByClassName('of-tos-text-part');

    for (var i = 0; i < tabs.length; i++) {
        tabs[i].onclick = function() {
            var target = this.getAttribute('data-role');
            for (var k = 0; k < tabs.length; k++) {
                tabs[k].classList.remove('of-tab-active');
                contents[k].classList.remove('of-content-active');
            }
            this.classList.add('of-tab-active');
            container.querySelector('[data-target='+target+']').classList.add('of-content-active');
        };
    }

    var nodes = document.querySelectorAll('[data-crm-widget=termsPopup]');
    for (var i = 0; i < nodes.length; i++) {
        nodes[i].onclick = function(e) {
            e.preventDefault();
            window.openCrmTermsPopup(this.getAttribute('data-tab'));
        };
    }

    window.openCrmTermsPopup = function(tab) {
        container.style.display = 'block';
        container.querySelector('[data-role=' + tab + ']').click();
        return false;
    };

    if (location.search.indexOf('tos=tos') != -1) {
        window.openCrmTermsPopup('tos');
    } else if (location.search.indexOf('tos=privacy') != -1) {
        window.openCrmTermsPopup('privacy');
    } else if (location.search.indexOf('tos=referral') != -1) {
        window.openCrmTermsPopup('referral');
    }
});
